import React, { Component } from 'react';
import TodoItem from './TodoItem';

export default class TodoList extends Component{
  render(){
    const { items} = this.props;
    return(
      <React.Fragment>

<table className="table table-bordered col-md-8 mx-auto table-striped table-hover">
  <thead>
    <tr>
      <th colSpan="3" className="h2 text-center alert alert-success" role="alert"><i className="fas fa-clipboard-list"></i> To Do List</th>
    </tr>
    <tr>
      <th scope="col">Work</th>
      <th scope="col">Update</th>
      <th scope="col">Remove</th>
    </tr>
  </thead>
  <tbody>
    <TodoItem/>
    <tr>
      <td colSpan="3" className="text-right"><button type="button" className="btn btn-danger text-capitalize"> <i className="fas fa-sync-alt"></i> Clear list </button></td>
    </tr>
  </tbody>
</table>

      </React.Fragment>
    )
  }
}
