import React, { Component } from 'react';

export default class TodoInput extends Component{
  render(){
    return(
      <tr>
        <td>
          <form>
          <div className="form-group">
            <input type="text" className="form-control" id="work" aria-describedby="workhelp" placeholder="Enter Task"/>
            <small id="workhelp" className="form-text text-muted">Put you'r daily task Here !</small>
          </div>
          <button type="submit" className="btn btn-primary">Add</button>
          </form>
        </td>
      </tr>
    )
  }
}
